
public class StringConcat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String st1 = "Coffee";
		String st2 = "Bread";
		
		String st3 = st1.concat(st2);
		String st4 = "Fresh".concat(st1);
		
		System.out.println(st3);
		System.out.println(st4);

	}

}
